$(document).ready(function() {
    $('#table').DataTable( {
        dom: 'Bfrtip',
        buttons: [
            {
                extend: 'print',
                exportOptions: {
                    columns: [ 0,1,5,6 ]
                },
                message: 'This print was produced using the Print button for DataTables',
 
        customize: function ( win ) { $(win.document.body) .css( 'font-size', '10pt' ) .prepend( '<img src="http://datatables.net/media/images/logo-fade.png" style="position:absolute; top:0; left:0;" />' ); 
        $(win.document.body).find('table').addClass('compact') .css('font-size', 'inherit'); 
            }       
        }]
    } );
} );